import React, {useState} from 'react'
import {List,Button,Drawer, Form, Input} from 'antd'

function ChatLobby() {
    state = {
        createLobbyVisible: false
    }

    const showCreateLobby = () => {
        useState({
            createLobbyVisible: true
        })
    }

    const onClose = () => {
        this.setState({
            createLobbyVisible: false
        })
    }

    const data = [
        {
            title: 'Lobby 1',
            description: 'description 1'
        },
        {
            title: 'Lobby 2',
            description: 'description 2'
        },
        {
            title: 'Lobby 3', 
            description: 'description 3'
        }

    ]

    return (
        <div>
            <Button onClick = {showCreateLobby}>
                Create Room
            </Button>
            <List
                itemLayout = 'horizontal'
                dataSource = {data}
                renderItem = {(item) => (
                    <List.Item>
                        <List.Item.Meta
                            title = {<a>{item.title}</a>}
                            description = {item.description}
                        />
                    </List.Item>
                )}
            >

            </List>

            <Drawer
                title = 'Create a chat room'
                width = {720}
                onClose = {onClose}
                visible = {this.state.createLobbyVisible}
            >
                <Form>
                    <Form.Item
                        name = 'roomname'
                        label = 'Room Name'
                        rules = {[{
                            required: true
                        }]}
                    >
                        <Input></Input>
                    </Form.Item>
                </Form>
            </Drawer>
        </div>
    )
}

export default ChatLobby
