import React from 'react'
import {List,Button,Drawer} from 'antd'

function ChatLobby() {

    state = {
        createLobbyVisible: false
    }

    showCreateLobby = () => {
        this.setState({
            createLobbyVisible: true
        })
    }

    onClose = () => {
        this.setState({
            createLobbyVisible: false
        })
    }

    const data = [
        {
            title: 'Lobby 1',
            description: 'description 1'
        },
        {
            title: 'Lobby 2',
            description: 'description 2'
        },
        {
            title: 'Lobby 3', 
            description: 'description 3'
        }

    ]

    return (
        <div>
            <Button onClick = {showCreateLobby}>
                Create Room
            </Button>
            <List
                itemLayout = 'horizontal'
                dataSource = {data}
                renderItem = {(item) => (
                    <List.Item>
                        <List.Item.Meta
                            title = {<a>{item.title}</a>}
                            description = {item.description}
                        />
                    </List.Item>
                )}
            >

            </List>
        </div>
    )
}

export default ChatLobby
